void applyTag(vector<long double>&, int, ui) {

}
vector<long double> combineValues(vector<long double> a, vector<long double> b) {
    int n = a.size();
    int m = b.size();
    vector<long double> A(n+m-1, 0);
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            A[i+j] += a[i] * b[j];
        }
    }
    return A;
};
using Seg = LSegTree<vector<long double>, int, &combineTag, &applyTag, &combineValues>;

class WaitingForBusAgain {
public:
    double expectedBus(vector <int> f) {
        int N = f.size();
        int m = *min_element(f.begin(),f.end());
        vector2<long double> D(N, 2);
        vector<double> sqs(N);
        for (int i = 0; i < N; ++i) {
            sqs[i] = sqrt((double)f[i]);
            D[i][0] = (f[i] - m) / sqs[i];
            D[i][1] = m / sqs[i];
        }

        Seg S(D, vector<long double>{1}, 0);

        long double ans = 0;
        for (int i = 0; i < N; ++i) {
            vector<long double> C{1};
            if (i != 0) C = combineValues(C, S.get(0, i-1));
            if (i != N-1) C = combineValues(C, S.get(i+1, N-1));
            long double s = 1;
            for (int j = 0; j < N; ++j) if (j != i) s *= sqs[j];
            for (auto&c:C) c /= s;

            long double cur = 0;
            for (int j = 0; j < N; ++j) cur += C[j] / (j+1);
            cur *= i * m;
            cur /= f[i];
            ans += cur;
        }
        return ans;
    }

    void solve(istream& cin, ostream& cout) {
        cout << fixed << setprecision(20) << endl;
        cout << expectedBus({8,8,8,8}) << endl;
        cout << expectedBus({1,2,3}) << endl;
        cout << expectedBus({3,2,1}) << endl;
        cout << expectedBus({3,1,4,1,5,9,2,6,5,3,5}) << endl;
        cout << expectedBus({26, 19548, 19299, 19431, 39568, 19476, 19726, 19530, 19613, 19529, 19679, 19552, 19706, 19505, 19666,
                             19349, 19755, 19424, 19486, 19596, 19439, 19500, 19639, 19520, 19472, 19675, 19602, 19552, 19933, 19424,
                             19316, 19497, 19582, 19531, 19679, 19742, 19896, 19365, 20083, 19542, 19827, 19583, 19592, 19593, 19661,
                             19340, 19403, 19664, 19690, 19655, 19727, 19661, 19528, 19836, 19819, 19414, 19770, 19500, 19653, 19613,
                             19579, 19794, 19668, 19729, 19428, 19477, 19733, 19641, 19680, 19594, 19667, 19878, 19571, 19627, 19576,
                             19422, 19521, 19512, 19481, 19620, 19364, 19487, 19740, 19627, 19795, 19353, 19251, 19865, 19710, 19597,
                             19610, 19710, 19491, 19598, 19650, 19828, 19726, 19810, 19766, 19692}) << endl;
        cout << expectedBus(vector<int>(1000, 1)) << endl;
    }
};


